export default function daysScreen(){const main=document.querySelector("main"),daysScreen=document.createElement("div"),gymApp=JSON.parse(localStorage.getItem("GymApp"));daysScreen.classList.add("days-screen"),daysScreen.innerHTML=`\n        <button class="days-screen__button">+ New Day</button>\n        <div class="days-screen__body">\n            <ul class="days-screen__list">\n            ${gymApp?gymApp.map(((day,index)=>`\n                    <li class="day-item">Day ${index+1}</li>\n                `)).join(""):""}\n            </ul>\n        </div>\n    `,main.appendChild(daysScreen)}